// src/pages/LoginPage.js

import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { BACKEND_API_BASE } from '../config';
import GoogleSignIn from '../components/GoogleSignIn';

const infoNestLogo = '/logo.png';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState(''); // For success/error messages
  const navigate = useNavigate(); // Hook for navigation
  const [showPassword, setShowPassword] = useState(false);

  const handleLogin = async (event) => {
    event.preventDefault();
    setMessage('');
    try {
      const response = await axios.post(`${BACKEND_API_BASE}/login`, { username, password });
      if (response.status === 200 && response.data.success) {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('username', username);
        setMessage('Login successful! Redirecting...');
        navigate('/home');
      } else {
        setMessage(response.data.error || 'Login failed. Please try again.');
      }
    } catch (error) {
      console.error('Login error:', error);
      if (error.response) {
        setMessage(error.response.data.error || 'Login failed: Invalid credentials.');
      } else if (error.request) {
        setMessage('Network error. Server might be down or unreachable.');
      } else {
        setMessage('An unexpected error occurred. Please try again.');
      }
    }
  };

  const togglePasswordVisibility = () => setShowPassword(!showPassword);

  // Google success handler
  const handleGoogleSuccess = (data) => {
    // data: { success, token, user }
    if (data && data.success) {
      localStorage.setItem('token', data.token);
      if (data.user && data.user.username) {
        localStorage.setItem('username', data.user.username);
      }
      setMessage('Google login successful! Redirecting...');
      navigate('/home');
    } else {
      setMessage('Google login failed.');
    }
  };

  const handleGoogleError = (errMsg) => {
    setMessage(errMsg || 'Google authentication failed.');
  };

  return (
    <div className="login-page-container" style={{
      fontFamily: 'Arial, sans-serif', margin: 0, backgroundColor: '#e6f4ea',
      display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center',
      minHeight: '100vh'
    }}>
      <header className="header" style={{
        display: 'flex', alignItems: 'center', gap: '10px', padding: '20px',
        position: 'absolute', top: 0, left: 0, width: '100%', boxSizing: 'border-box'
      }}>
        <img src={infoNestLogo} alt="InfoNest Logo" className="circle-logo"
             style={{ width: '50px', height: '50px', borderRadius: '50%', objectFit: 'cover' }} />
        <h1 className="page-title" style={{ fontSize: '24px', fontWeight: 'bold', color: '#1a202c' }}>
          InfoNest Login
        </h1>
      </header>

      <main className="form-container" style={{
        display: 'flex', justifyContent: 'center', alignItems: 'center', flexGrow: 1
      }}>
        <div className="form-box" style={{
          background: '#d4f5d3', padding: '30px', borderRadius: '20px', width: '300px',
          boxShadow: '0 0 10px rgba(0,0,0,0.1)'
        }}>
          <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Welcome Back</h2>
          <form onSubmit={handleLogin}>
            <input
              type="text"
              placeholder="Username"
              value={username}
              autoComplete="username"
              onChange={(e) => setUsername(e.target.value)}
              required
              style={{
                width: '100%', padding: '10px', marginBottom: '15px',
                border: '1px solid #ccc', borderRadius: '5px'
              }}
            />
            <div style={{ position: 'relative', marginBottom: '15px' }}>
              <input
                type={showPassword ? 'text' : 'password'}
                placeholder="Password"
                value={password}
                autoComplete="current-password"
                onChange={(e) => setPassword(e.target.value)}
                required
                style={{
                  width: '100%', padding: '10px', paddingRight: '40px',
                  border: '1px solid #ccc', borderRadius: '5px'
                }}
              />
              <span
                onClick={togglePasswordVisibility}
                style={{
                  position: 'absolute', right: '10px', top: '50%',
                  transform: 'translateY(-50%)', cursor: 'pointer',
                  color: '#4CAF50', fontSize: '14px', fontWeight: 'bold'
                }}
              >
                {showPassword ? 'Hide' : 'Show'}
              </span>
            </div>
            <button
              type="submit"
              style={{
                width: '100%', padding: '10px',
                backgroundColor: '#4CAF50', color: '#fff', border: 'none',
                borderRadius: '5px', cursor: 'pointer', fontWeight: 'bold'
              }}
            >
              Login
            </button>
          </form>

          <GoogleSignIn
  onSuccess={(data) => {
    localStorage.setItem('token', data.token);
    if (data.user?.username) localStorage.setItem('username', data.user.username);
    setMessage('Google login successful! Redirecting...');
    navigate('/home');
  }}
  onError={(msg) => setMessage(msg || 'Google login failed.')}
/>

          <p style={{ marginTop: '15px', textAlign: 'center', fontSize: '14px' }}>
            Don&apos;t have an account? <Link to="/register" style={{ color: '#4CAF50' }}>Register</Link>
          </p>
        </div>
      </main>
    </div>
  );
};

export default LoginPage;
