// src/pages/LoginPage.js

import React, { useState, useEffect } from "react"; // <-- added useEffect here
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { BACKEND_API_BASE } from "../config";
// import GoogleSignIn from "../components/GoogleSignIn"; // Temporarily comment out while testing inline Google button

const infoNestLogo = "/logo.png";

const LoginPage = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (event) => {
    event.preventDefault();
    setMessage("");
    try {
      const r = await fetch(`${BACKEND_API_BASE}/auth/google`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ idToken: resp.credential }),
      });
      const data = await r.json();
      console.log("Backend auth response", data);
      if (data.success) {
        localStorage.setItem("token", data.token);
        localStorage.setItem("username", data.user.username);
        navigate("/home");
      } else {
        setMessage(data.error || "Google auth failed.");
      }
    } catch (e) {
      console.error("Network error calling backend", e);
      setMessage("Network error during Google auth.");
    }
    try {
      const response = await axios.post(`${BACKEND_API_BASE}/login`, {
        username,
        password,
      });
      if (response.status === 200 && response.data.success) {
        localStorage.setItem("token", response.data.token);
        localStorage.setItem("username", username);
        setMessage("Login successful! Redirecting...");
        navigate("/home");
      } else {
        setMessage(response.data.error || "Login failed. Please try again.");
      }
    } catch (error) {
      console.error("Login error:", error);
      if (error.response) {
        setMessage(
          error.response.data.error || "Login failed: Invalid credentials."
        );
      } else if (error.request) {
        setMessage("Network error. Server might be down or unreachable.");
      } else {
        setMessage("An unexpected error occurred. Please try again.");
      }
    }
  };

  const togglePasswordVisibility = () => setShowPassword(!showPassword);

  // TEMP Google diagnostic hook
  useEffect(() => {
    console.log("CID runtime test:", process.env.REACT_APP_GOOGLE_CLIENT_ID);
    const s = document.createElement("script");
    s.src = "https://accounts.google.com/gsi/client";
    s.async = true;
    s.defer = true;
    s.onload = () => {
      if (!window.google) {
        console.error("Google script not available");
        return;
      }
      window.google.accounts.id.initialize({
        client_id: process.env.REACT_APP_GOOGLE_CLIENT_ID,
        callback: (resp) => {
          console.log(
            "Credential callback fired:",
            resp && resp.credential ? "has token" : "no token",
            resp
          );
          // After confirming working, add backend POST here:
          // fetch(`${BACKEND_API_BASE}/auth/google`, {
          //   method: 'POST',
          //   headers: { 'Content-Type': 'application/json' },
          //   body: JSON.stringify({ idToken: resp.credential })
          // }).then(r => r.json()).then(d => console.log('Backend response', d));
        },
      });
      window.google.accounts.id.renderButton(
        document.getElementById("gsi-test-btn"),
        { theme: "outline", size: "large" }
      );
    };
    document.head.appendChild(s);
    return () => s.remove();
  }, []);

  return (
    <div
      style={{
        fontFamily: "Arial, sans-serif",
        margin: 0,
        backgroundColor: "#e6f4ea",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <header
        style={{
          display: "flex",
          alignItems: "center",
          gap: "10px",
          padding: "20px",
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          boxSizing: "border-box",
        }}
      >
        <img
          src={infoNestLogo}
          alt="InfoNest Logo"
          style={{
            width: "50px",
            height: "50px",
            borderRadius: "50%",
            objectFit: "cover",
          }}
        />
        <h1 style={{ fontSize: "24px", fontWeight: "bold", color: "#1a202c" }}>
          InfoNest Login
        </h1>
      </header>

      <main
        style={{
          flexGrow: 1,
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          width: "100%",
        }}
      >
        <div
          style={{
            background: "#d4f5d3",
            padding: "30px",
            borderRadius: "20px",
            width: "300px",
            boxShadow: "0 0 10px rgba(0,0,0,0.1)",
          }}
        >
          <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
            Welcome Back
          </h2>
          <form onSubmit={handleLogin}>
            <input
              type="text"
              placeholder="Username"
              value={username}
              autoComplete="username"
              onChange={(e) => setUsername(e.target.value)}
              required
              style={{
                width: "100%",
                padding: "10px",
                marginBottom: "15px",
                border: "1px solid #ccc",
                borderRadius: "5px",
              }}
            />
            <div style={{ position: "relative", marginBottom: "15px" }}>
              <input
                type={showPassword ? "text" : "password"}
                placeholder="Password"
                value={password}
                autoComplete="current-password"
                onChange={(e) => setPassword(e.target.value)}
                required
                style={{
                  width: "100%",
                  padding: "10px",
                  paddingRight: "40px",
                  border: "1px solid #ccc",
                  borderRadius: "5px",
                }}
              />
              <span
                onClick={togglePasswordVisibility}
                style={{
                  position: "absolute",
                  right: "10px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  cursor: "pointer",
                  color: "#4CAF50",
                  fontSize: "14px",
                  fontWeight: "bold",
                }}
              >
                {showPassword ? "Hide" : "Show"}
              </span>
            </div>
            <button
              type="submit"
              style={{
                width: "100%",
                padding: "10px",
                backgroundColor: "#4CAF50",
                color: "#fff",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
                fontWeight: "bold",
              }}
            >
              Login
            </button>
          </form>

          {/* Google test button container */}
          <div id="gsi-test-btn" style={{ marginTop: 16 }} />

          {message && (
            <p
              style={{
                marginTop: "15px",
                textAlign: "center",
                fontSize: "14px",
                color: message.toLowerCase().includes("success")
                  ? "green"
                  : "red",
              }}
            >
              {message}
            </p>
          )}

          <p
            style={{ marginTop: "15px", textAlign: "center", fontSize: "14px" }}
          >
            Don&apos;t have an account?{" "}
            <span style={{ color: "#4CAF50" }}>
              <a
                href="/register"
                style={{ color: "#4CAF50", textDecoration: "none" }}
              >
                Register
              </a>
            </span>
          </p>
        </div>
      </main>
    </div>
  );
};

export default LoginPage;
