// src/pages/HomePage.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // For redirection
import axios from 'axios'; // For making API calls
import '../styles/Home.css'; // Import the CSS file

// Placeholder images
const infonestLogo = '/logo.png'; // Assuming logo.png is in public/
const userAvatar = '/avatar.png'; // Assuming avatar.png is in public/

const HomePage = () => {
  const navigate = useNavigate(); // Hook for navigation
  const [username, setUsername] = useState('User');

  // --- State for managing multiple chats ---
  const [chats, setChats] = useState([]); // Stores multiple chat sessions [{ id: 1, title: 'Chat 1', messages: [...] }]
  const [currentChatId, setCurrentChatId] = useState(null); // ID of the chat currently being displayed
  const [messages, setMessages] = useState([]); // Messages for the current chat
  const [sidebarHistory, setSidebarHistory] = useState([]); // Stores { id, title } for sidebar

  const API_BASE_URL = 'http://localhost:8000/api';

  // --- Effect to load initial chats on mount ---
  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUsername = localStorage.getItem('username');

    if (!token) {
      alert('You are not logged in. Please log in first.');
      navigate('/login');
      return;
    }

    if (storedUsername) {
      setUsername(storedUsername);
    }

    // Load initial chats (e.g., start a new chat by default)
    loadInitialChats(token);
  }, [navigate]); // Depend on navigate for redirects

  // --- Function to load initial chats ---
  // In a real app, this would fetch chat lists from the backend.
  // For now, it just initializes with a new chat.
  const loadInitialChats = async (token) => {
    // Simulate creating the first chat
    handleNewChat();
  };

  // --- Helper to get messages for the current chat ---
  const getCurrentChatMessages = () => {
    const currentChat = chats.find(chat => chat.id === currentChatId);
    return currentChat ? currentChat.messages : [];
  };

  // --- Helper to update messages for the current chat in the 'chats' state ---
  const updateCurrentChatMessages = (newMessages) => {
    setChats(prevChats =>
      prevChats.map(chat =>
        chat.id === currentChatId ? { ...chat, messages: newMessages } : chat
      )
    );
    // Also update the displayed messages state directly
    setMessages(newMessages);
  };

  // --- Function to fetch a specific chat's history ---
  // Assumes backend has an endpoint like /api/chat/{chatId}
  const fetchChatHistory = async (chatId) => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(`${API_BASE_URL}/chat/${chatId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.status === 200) {
        const fetchedMessages = response.data.data.messages; // Assuming backend returns messages array
        setMessages(fetchedMessages); // Update the messages for the currently displayed chat
        // Update sidebar history titles from the 'chats' state
        setSidebarHistory(chats.map(chat => ({ id: chat.id, title: chat.title })));
      } else {
        console.error(`Failed to fetch chat history for ${chatId}:`, response.data.error);
        alert('Could not load chat history.');
      }
    } catch (error) {
      console.error('Network error fetching chat history:', error);
      if (error.response && error.response.status === 401) {
        alert('Session expired. Please log in again.');
        localStorage.removeItem('token');
        localStorage.removeItem('username');
        navigate('/login');
      } else {
        alert('Network error. Could not connect to server for history.');
      }
    }
  };

  // --- Function to send message and update current chat ---
  const sendMessage = async (messageText) => {
    const token = localStorage.getItem('token');
    if (!token || messageText.trim() === '' || !currentChatId) return;

    const userMessage = { sender: 'user', text: messageText };
    const currentMessages = getCurrentChatMessages();

    // Optimistic update of the current chat's messages
    updateCurrentChatMessages([...currentMessages, userMessage]);

    setChatInput(''); // Clear input field

    try {
      // Assuming your chat endpoint can work with a chatId (e.g., POST /api/chat/{chatId})
      const response = await axios.post(`${API_BASE_URL}/chat/${currentChatId}`,
        { message: messageText },
        { headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'application/json' } }
      );

      if (response.status === 200) {
        const botResponse = response.data.botResponse;
        const newMessages = [...currentMessages, userMessage, { sender: 'bot', text: botResponse }];
        updateCurrentChatMessages(newMessages);

        // Update sidebar title if it's a new chat and needs a title generated
        // This usually happens when the first significant message is sent.
        // For now, we rely on titles set during 'New Chat' or from backend.
      } else {
        const errorData = response.data;
        const errorMsg = { sender: 'bot', text: `Error: ${errorData.error || 'Failed to get bot response.'}` };
        updateCurrentChatMessages([...currentMessages, userMessage, errorMsg]);
      }
    } catch (error) {
      console.error('Network error sending message:', error);
      let errorMessage = 'Network error. Could not connect to chatbot.';
      if (error.response && error.response.status === 401) {
         errorMessage = 'Session expired. Please log in again.';
         localStorage.removeItem('token');
         localStorage.removeItem('username');
         navigate('/login');
      } else if (error.response && error.response.data && error.response.data.error) {
         errorMessage = `Error: ${error.response.data.error}`;
      }
      updateCurrentChatMessages([...currentMessages, userMessage, { sender: 'bot', text: errorMessage }]);
    }
  };

  // --- Function to handle starting a new chat ---
  const handleNewChat = () => {
    const newChatId = Date.now(); // Simple unique ID for the new chat
    const newChat = {
      id: newChatId,
      title: `New Chat ${chats.length + 1}`, // Placeholder title
      messages: [{ sender: 'bot', text: 'Hello! How can I help you today?' }]
    };

    // Add the new chat to the list of chats
    setChats([...chats, newChat]);
    // Set this new chat as the current one
    setCurrentChatId(newChatId);
    // Update the displayed messages and sidebar history
    setMessages(newChat.messages);
    setSidebarHistory([...sidebarHistory, { id: newChatId, title: newChat.title }]);
    setChatInput(''); // Clear the input field
  };

  // --- Function to handle clicking a recent chat from the sidebar ---
  const handleRecentChatClick = (chatId) => {
    // Find the clicked chat in the 'chats' state
    const clickedChat = chats.find(chat => chat.id === chatId);
    if (clickedChat) {
      setCurrentChatId(chatId); // Set this as the current chat
      setMessages(clickedChat.messages); // Load its messages
      setChatInput(''); // Clear input field
      // No need to update sidebarHistory here as it's just a display of what's in 'chats'
    }
  };

  const handleChatInputChange = (e) => {
    setChatInput(e.target.value);
  };

  const handleSendButtonClick = () => {
    sendMessage(chatInput);
  };

  const handleInputKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage(chatInput);
    }
  };

  const handleQuickQuestionClick = (e) => {
    sendMessage(e.target.textContent);
  };

  // Auto-scroll messages to bottom
  useEffect(() => {
    const messagesContainer = document.querySelector('.chat-messages-container');
    if (messagesContainer) {
      messagesContainer.scrollTop = messagesContainer.scrollHeight;
    }
  }, [messages]); // Scroll whenever messages state updates


  return (
    <div className="container">
      <aside className="sidebar">
        <div className="logo-section">
          <img src={infonestLogo} alt="InfoNest Logo" className="circle-logo" />
          <h1>InfoNest</h1>
        </div>

        {/* Button to start a new chat */}
        <button className="new-chat-btn" onClick={handleNewChat}>
          {/* Pencil SVG for New Chat */}
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M12 5V19M19 12H5" stroke="#333" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          New chat
        </button>

        <p className="recent-title">Recent</p>
        <ul className="recent-list">
          {/* Mapping through sidebarHistory to display recent chats */}
          {sidebarHistory.map((chatInfo) => (
            <li
              key={chatInfo.id}
              onClick={() => handleRecentChatClick(chatInfo.id)}
              // Highlight the currently active chat
              style={{
                fontWeight: currentChatId === chatInfo.id ? 'bold' : 'normal',
                backgroundColor: currentChatId === chatInfo.id ? '#e0e0e0' : 'transparent'
              }}
            >
              {chatInfo.title}
            </li>
          ))}
        </ul>

        <div className="settings-footer">
          {/* Gear SVG for Settings */}
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M19.4286 4.57143C19.4286 4.57143 15.2143 0.571429 12 0.571429C8.78571 0.571429 4.57143 4.57143 4.57143 4.57143M19.4286 4.57143V10.2857C19.4286 10.7857 19.0714 11.1429 18.5714 11.1429H17.2857M19.4286 4.57143V8.57143M4.57143 4.57143V10.2857C4.57143 10.7857 4.92857 11.1429 5.42857 11.1429H6.71429M4.57143 4.57143V8.57143M12 11.1429V23.4286M12 11.1429C10.2857 11.1429 8.57143 11.1429 7.14286 10.2857C5.71429 9.42857 5.71429 7.71429 5.71429 6.28571C5.71429 4.85714 5.71429 3.14286 7.14286 2.28571C8.57143 1.42857 10.2857 1.42857 12 1.42857C13.7143 1.42857 15.4286 1.42857 16.8571 2.28571C18.2857 3.14286 18.2857 4.85714 18.2857 6.28571C18.2857 7.71429 18.2857 9.42857 16.8571 10.2857" stroke="#555" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          Settings & Help
        </div>
      </aside>

      <main className="main-content">
        {/* Chat messages display area */}
        <div className="chat-messages-container">
          <ul>
            {messages.map((msg, index) => (
              <li
                key={index} // Using index here. For production, use a stable unique ID per message if possible.
                className={msg.sender === 'user' ? 'user-message' : 'bot-message'}
              >
                {msg.text}
              </li>
            ))}
          </ul>
        </div>

        <div className="ask-container">
          <input
            type="text"
            id="chatInput"
            placeholder="Ask InfoNest anything"
            value={chatInput}
            onChange={handleChatInputChange}
            onKeyPress={handleInputKeyPress}
          />
          <button id="sendChatBtn" onClick={handleSendButtonClick}>Send</button>
        </div>
      </main>
    </div>
  );
};

export default HomePage;
