// src/pages/HomePage.js (modified for conversation management)

import React, { useState, useEffect, useRef } from 'react'; // Import useRef
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Home.css';

const infonestLogo = '/logo.png';
const userAvatar = '/avatar.png';

const HomePage = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('User');
  const [messages, setMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [sidebarHistory, setSidebarHistory] = useState([]); // Stores { id, snippet }
  const [currentConversationId, setCurrentConversationId] = useState(null); // State to track current conversation

  const API_BASE_URL = 'http://localhost:8000/api';

  // Use a ref to ensure fetchChatHistory isn't called infinitely if messages.length changes
  const initialLoadDone = React.useRef(false);


  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUsername = localStorage.getItem('username');

    if (!token) {
      alert('You are not logged in. Please log in first.');
      navigate('/login');
      return;
    }

    if (storedUsername) {
      setUsername(storedUsername);
    }

    // Fetch conversations when component mounts, but only once
    if (!initialLoadDone.current) {
        fetchConversations(token);
        initialLoadDone.current = true;
    }
  }, [navigate]); // Depend on navigate

  // --- Function to Fetch List of Conversations for Sidebar ---
  const fetchConversations = async (token) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/conversations`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.status === 200 && response.data.success) {
        const conversations = response.data.data;
        setSidebarHistory(conversations); // Set sidebar history
        // If there are conversations, load the most recent one by default
        if (conversations.length > 0) {
          loadConversation(conversations[0].id, token); // Load the first one
        } else {
          // If no conversations, show initial greeting
          setMessages([{ sender: 'bot', text: 'Hello! How can I help you today?' }]);
        }
      } else {
        console.error('Failed to fetch conversations:', response.data.error);
        alert('Could not load conversation history. Please try again.');
        setMessages([{ sender: 'bot', text: 'Hello! How can I help you today?' }]);
      }
    } catch (error) {
      console.error('Network error fetching conversations:', error);
      if (error.response && error.response.status === 401) {
        alert('Session expired. Please log in again.');
        localStorage.removeItem('token');
        localStorage.removeItem('username');
        navigate('/login');
      } else {
        alert('Network error. Could not connect to server for conversations.');
        setMessages([{ sender: 'bot', text: 'Hello! How can I help you today?' }]);
      }
    }
  };

  // --- Function to Fetch Messages for a Specific Conversation ---
  const loadConversation = async (conversationId, token) => {
    if (!conversationId) return; // Don't do anything if no ID
    setCurrentConversationId(conversationId); // Set the current conversation
    try {
      const response = await axios.get(`${API_BASE_URL}/conversation/${conversationId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.status === 200 && response.data.success) {
        const fetchedMessages = [];
        response.data.data.forEach(entry => {
          if (entry.userMessage) { // User message
            formattedMessages.push({ sender: 'user', text: entry.userMessage });
          }
          if (entry.botResponse) { // Bot response
            formattedMessages.push({ sender: 'bot', text: entry.botResponse });
          }
        });
        setMessages(formattedMessages);
      } else {
        console.error('Failed to fetch specific conversation:', response.data.error);
        alert('Could not load this conversation history.');
      }
    } catch (error) {
      console.error('Network error fetching conversation:', error);
      alert('Network error. Could not load conversation.');
    }
  };

  // --- Function to Send Message to Chatbot ---
  const sendMessage = async (messageText) => {
    const token = localStorage.getItem('token');
    if (!token || messageText.trim() === '' || !currentConversationId) return; // Require token and conversation ID

    const userMessage = { sender: 'user', text: messageText };
    setMessages((prevMessages) => [...prevMessages, userMessage]);

    setChatInput(''); // Clear input field

    try {
      const response = await axios.post(`${API_BASE_URL}/chat/send`, // Use the new /send endpoint
        { message: messageText, conversationId: currentConversationId }, // Send message and conversation ID
        { headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'application/json' } }
      );

      if (response.status === 200) {
        const botResponse = response.data.botResponse;
        setMessages((prevMessages) => [...prevMessages, { sender: 'bot', text: botResponse }]);
        // Update sidebar history with the latest user message
        setSidebarHistory(prevSidebar => {
          const truncatedMsg = messageText.substring(0, 30) + (messageText.length > 30 ? '...' : '');
          // Add to the beginning if it's a new message, avoid duplicates
          if (prevSidebar.length === 0 || prevSidebar[0] !== truncatedMsg) {
            return [truncatedMsg, ...prevSidebar.slice(0, 4)]; // Add to front, keep limit
          }
          return prevSidebar;
        });
      } else {
        const errorData = response.data;
        setMessages((prevMessages) => [...prevMessages, { sender: 'bot', text: `Error: ${errorData.error || 'Failed to get bot response.'}` }]);
      }
    } catch (error) {
      console.error('Network error sending message:', error);
      let errorMessage = 'Network error. Could not connect to chatbot.';
      if (error.response) {
        if (error.response.status === 401) { // Session expired
           errorMessage = 'Session expired. Please log in again.';
           localStorage.removeItem('token');
           localStorage.removeItem('username');
           navigate('/login');
        } else if (error.response.data && error.response.data.error) {
           errorMessage = `Error: ${error.response.data.error}`;
        }
      }
      setMessages((prevMessages) => [...prevMessages, { sender: 'bot', text: errorMessage }]);
    }
  };

  // --- Handle "New chat" button ---
  const handleNewChat = async () => {
    setMessages([]); // Clear current chat display
    setSidebarHistory([]); // Clear sidebar history

    // Call backend to start a new conversation
    const token = localStorage.getItem('token');
    if (!token) return; // Should not happen if auth is working

    try {
      const response = await axios.post(`${API_BASE_URL}/chat/new`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (response.status === 201) {
        setCurrentConversationId(response.data.conversationId);
        setMessages([{ sender: 'bot', text: response.data.initialMessage || 'Starting a new chat...' }]);
        // Sidebar history will be updated when conversations are fetched again,
        // or you could manually add a placeholder if needed.
      } else {
        alert('Failed to start new chat. Please try again.');
      }
    } catch (error) {
      console.error('Error starting new chat:', error);
      alert('Network error starting new chat.');
    }
  };

  // --- Event Handlers for Input and Buttons ---
  const handleChatInputChange = (e) => {
    setChatInput(e.target.value);
  };

  const handleSendButtonClick = () => {
    sendMessage(chatInput);
  };

  const handleInputKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage(chatInput);
    }
  };

  // --- Handle Clicking on Recent History Items ---
  const handleRecentChatItemClick = (conversationId) => {
    const token = localStorage.getItem('token');
    if (!token) return; // Should not happen if auth is working

    console.log("Clicked on recent chat item with ID:", conversationId);
    loadConversation(conversationId, token); // Load this conversation
  };

  // --- Auto-scroll messages to bottom ---
  useEffect(() => {
    const messagesContainer = document.querySelector('.chat-messages-container ul');
    if (messagesContainer) {
      messagesContainer.scrollTop = messagesContainer.scrollHeight;
    }
  }, [messages]); // Re-run when messages state updates

  return (
    <div className="container">
      <aside className="sidebar">
        <div className="logo-section">
          <img src={infonestLogo} alt="InfoNest Logo" className="circle-logo" />
          <h1>InfoNest</h1>
        </div>

        <button className="new-chat-btn" onClick={handleNewChat}>
          {/* SVG for New Chat Icon */}
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ marginRight: '8px' }}>
            <path d="M12 5V19M19 12H5" stroke="#333" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          New chat
        </button>

        <p className="recent-title">Recent</p>
        <ul className="recent-list">
          {/* Render recent conversations (using conversation ID and snippet) */}
          {sidebarHistory.map((conv) => ( // Use conv object, not just entry
            <li
              key={conv.id} // Use conversation ID as key
              onClick={() => handleRecentChatItemClick(conv.id)} // Pass conversation ID
              style={{
                cursor: 'pointer', padding: '5px 0', fontSize: '14px', lineHeight: '1.5', color: '#333',
                fontWeight: currentConversationId === conv.id ? 'bold' : 'normal' // Highlight active chat
              }}
            >
              {conv.snippet}
            </li>
          ))}
        </ul>

        <div className="settings-footer">
          {/* SVG for Settings Icon */}
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ marginRight: '8px' }}>
            <path d="M19.4286 4.57143C19.4286 4.57143 15.2143 0.571429 12 0.571429C8.78571 0.571429 4.57143 4.57143 4.57143 4.57143M19.4286 4.57143V10.2857C19.4286 10.7857 19.0714 11.1429 18.5714 11.1429H17.2857M19.4286 4.57143V8.57143M4.57143 4.57143V10.2857C4.57143 10.7857 4.92857 11.1429 5.42857 11.1429H6.71429M4.57143 4.57143V8.57143M12 11.1429V23.4286M12 11.1429C10.2857 11.1429 8.57143 11.1429 7.14286 10.2857C5.71429 9.42857 5.71429 7.71429 5.71429 6.28571C5.71429 4.85714 5.71429 3.14286 7.14286 2.28571C8.57143 1.42857 10.2857 1.42857 12 1.42857C13.7143 1.42857 15.4286 1.42857 16.8571 2.28571C18.2857 3.14286 18.2857 4.85714 18.2857 6.28571C18.2857 7.71429 18.2857 9.42857 16.8571 10.2857" stroke="#555" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
      </svg>
      Settings & Help
    </div>
  </aside>

  <main className="main-content">
    {/* Greeting */}
    <div className="avatar-container">
      <img src={userAvatar} alt="User Avatar" className="avatar-img" />
    </div>
    <h1 className="greeting">Hello, <span id="userNameGreeting">{username}</span>!</h1>

    {/* Chat messages display area */}
    <div className="chat-messages-container">
      <ul id="messagesList">
        {messages.map((msg, index) => (
          <li
            key={index}
            className={msg.sender === 'user' ? 'user-message' : 'bot-message'}
          >
            {msg.text}
          </li>
        ))}
      </ul>
    </div>

    {/* Input area */}
    <div className="ask-container">
      <input
        type="text"
        id="chatInput"
        placeholder="Ask InfoNest anything"
        value={chatInput}
        onChange={handleChatInputChange}
        onKeyPress={handleInputKeyPress}
      />
      <button id="sendChatBtn" onClick={handleSendButtonClick}>Send</button>
    </div>
  </main>
</div>); };

export default HomePage;
