// src/pages/LoginPage.js

import React, { useState, useEffect, useRef, useCallback } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { BACKEND_API_BASE } from "../config";

const infoNestLogo = "/logo.png";
const GOOGLE_SCRIPT_SRC = "https://accounts.google.com/gsi/client";
const GOOGLE_SCRIPT_ID = "google-identity-script";

const LoginPage = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [msgType, setMsgType] = useState(""); // 'success' | 'error' | ''
  const [showPassword, setShowPassword] = useState(false);
  const [loginLoading, setLoginLoading] = useState(false);
  const [googleLoading, setGoogleLoading] = useState(false);

  const navigate = useNavigate();
  const googleInitRef = useRef(false);

  const setStatusMessage = (text, type = "error") => {
    setMessage(text);
    setMsgType(type);
  };

  const handleLogin = async (event) => {
    event.preventDefault();
    setStatusMessage("", "");
    setLoginLoading(true);

    try {
      const response = await axios.post(`${BACKEND_API_BASE}/login`, {
        username,
        password,
      });

      if (response.status === 200 && response.data.success) {
        localStorage.setItem("token", response.data.token);
        localStorage.setItem("username", username);
        setStatusMessage("Login successful! Redirecting...", "success");
        navigate("/home");
      } else {
        setStatusMessage(
          response.data.error || "Login failed. Please try again.",
          "error"
        );
      }
    } catch (error) {
      console.error("Login error:", error);
      if (error.response) {
        setStatusMessage(
          error.response.data.error || "Login failed: Invalid credentials.",
          "error"
        );
      } else if (error.request) {
        setStatusMessage(
          "Network error. Server might be down or unreachable.",
          "error"
        );
      } else {
        setStatusMessage(
          "An unexpected error occurred. Please try again.",
          "error"
        );
      }
    } finally {
      setLoginLoading(false);
    }
  };

  const togglePasswordVisibility = () => setShowPassword((p) => !p);

  const handleGoogleCredential = useCallback(
    async (idToken) => {
      if (!idToken) {
        setStatusMessage("Google did not return a credential.", "error");
        return;
      }
      setGoogleLoading(true);
      setStatusMessage("", "");
      try {
        const res = await axios.post(`${BACKEND_API_BASE}/auth/google`, {
          idToken,
        });
        if (res.data?.success) {
            localStorage.setItem("token", res.data.token);
            if (res.data.user?.username) {
              localStorage.setItem("username", res.data.user.username);
            }
            setStatusMessage("Google sign-in successful! Redirecting...", "success");
            navigate("/home");
        } else {
          setStatusMessage(res.data?.error || "Google authentication failed.", "error");
        }
      } catch (e) {
        console.error("Google auth backend error:", e);
        setStatusMessage("Network or server error during Google auth.", "error");
      } finally {
        setGoogleLoading(false);
      }
    },
    [navigate]
  );

  // One-time Google Identity Services initialization
  useEffect(() => {
    if (googleInitRef.current) return;
    googleInitRef.current = true;

    const clientId = process.env.REACT_APP_GOOGLE_CLIENT_ID;

    if (!clientId) {
      setStatusMessage(
        "Google Client ID not configured. Set REACT_APP_GOOGLE_CLIENT_ID in the frontend .env and restart the dev server.",
        "error"
      );
      return;
    }

    // Add script only once
    if (!document.getElementById(GOOGLE_SCRIPT_ID)) {
      const script = document.createElement("script");
      script.id = GOOGLE_SCRIPT_ID;
      script.src = GOOGLE_SCRIPT_SRC;
      script.async = true;
      script.defer = true;
      script.onload = () => {
        if (!window.google || !window.google.accounts?.id) {
          setStatusMessage("Google script loaded but API not available.", "error");
          return;
        }
        initializeGoogle(clientId);
      };
      script.onerror = () =>
        setStatusMessage("Failed to load Google identity script.", "error");
      document.head.appendChild(script);
    } else {
      if (window.google && window.google.accounts?.id) {
        initializeGoogle(clientId);
      }
    }

    function initializeGoogle(clientIdValue) {
      try {
        window.google.accounts.id.initialize({
          client_id: clientIdValue,
            callback: (resp) => {
              if (resp?.credential) {
                handleGoogleCredential(resp.credential);
              } else {
                setStatusMessage("No Google credential received.", "error");
              }
            },
        });

        const btnContainer = document.getElementById("gsi-btn-slot");
        if (btnContainer) {
          window.google.accounts.id.renderButton(btnContainer, {
            theme: "outline",
            size: "large",
            width: 260,
            type: "standard",
          });
        }
      } catch (e) {
        console.error("Google initialize error:", e);
        setStatusMessage("Failed to initialize Google sign-in.", "error");
      }
    }
  }, [handleGoogleCredential]);

  const isBusy = loginLoading || googleLoading;

  return (
    <div
      style={{
        fontFamily: "Arial, sans-serif",
        margin: 0,
        backgroundColor: "#e6f4ea",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <header
        style={{
          display: "flex",
          alignItems: "center",
          gap: "10px",
          padding: "20px",
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          boxSizing: "border-box",
        }}
      >
        <img
          src={infoNestLogo}
          alt="InfoNest Logo"
          style={{
            width: "50px",
            height: "50px",
            borderRadius: "50%",
            objectFit: "cover",
          }}
        />
        <h1 style={{ fontSize: "24px", fontWeight: "bold", color: "#1a202c" }}>
          InfoNest Login
        </h1>
      </header>

      <main
        style={{
          flexGrow: 1,
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          width: "100%",
        }}
      >
        <div
          style={{
            background: "#d4f5d3",
            padding: "30px",
            borderRadius: "20px",
            width: "320px",
            boxShadow: "0 0 10px rgba(0,0,0,0.1)",
          }}
        >
          <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
            Welcome Back
          </h2>
          <form onSubmit={handleLogin}>
            <input
              type="text"
              placeholder="Username"
              value={username}
              autoComplete="username"
              onChange={(e) => setUsername(e.target.value)}
              required
              disabled={isBusy}
              style={{
                width: "100%",
                padding: "10px",
                marginBottom: "15px",
                border: "1px solid #ccc",
                borderRadius: "5px",
                background: isBusy ? "#f3f3f3" : "#fff",
              }}
            />
            <div style={{ position: "relative", marginBottom: "15px" }}>
              <input
                type={showPassword ? "text" : "password"}
                placeholder="Password"
                value={password}
                autoComplete="current-password"
                onChange={(e) => setPassword(e.target.value)}
                required
                disabled={isBusy}
                style={{
                  width: "100%",
                  padding: "10px",
                  paddingRight: "50px",
                  border: "1px solid #ccc",
                  borderRadius: "5px",
                  background: isBusy ? "#f3f3f3" : "#fff",
                }}
              />
              <button
                type="button"
                onClick={togglePasswordVisibility}
                disabled={isBusy}
                style={{
                  position: "absolute",
                  right: "10px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  cursor: "pointer",
                  background: "none",
                  border: "none",
                  color: "#4CAF50",
                  fontSize: "12px",
                  fontWeight: "bold",
                  padding: 0,
                }}
              >
                {showPassword ? "Hide" : "Show"}
              </button>
            </div>
            <button
              type="submit"
              disabled={loginLoading}
              style={{
                width: "100%",
                padding: "10px",
                backgroundColor: loginLoading ? "#6fbf74" : "#4CAF50",
                color: "#fff",
                border: "none",
                borderRadius: "5px",
                cursor: loginLoading ? "default" : "pointer",
                fontWeight: "bold",
                transition: "background 0.2s",
              }}
            >
              {loginLoading ? "Logging in..." : "Login"}
            </button>
          </form>

            <div
              style={{
                marginTop: "18px",
                display: "flex",
                flexDirection: "column",
                gap: "10px",
                alignItems: "center",
              }}
            >
              <div id="gsi-btn-slot" />
              <button
                type="button"
                disabled={googleLoading}
                onClick={() => {
                  if (window.google?.accounts?.id) {
                    window.google.accounts.id.prompt(); // optional manual prompt
                  } else {
                    setStatusMessage("Google not ready yet.", "error");
                  }
                }}
                style={{
                  width: "100%",
                  padding: "9px",
                  background: "#fff",
                  border: "1px solid #4285F4",
                  color: "#4285F4",
                  fontWeight: "bold",
                  borderRadius: "5px",
                  cursor: googleLoading ? "default" : "pointer",
                  fontSize: "14px",
                }}
              >
                {googleLoading ? "Authorizing..." : "Continue with Google"}
              </button>
            </div>

          {message && (
            <p
              style={{
                marginTop: "15px",
                textAlign: "center",
                fontSize: "14px",
                color: msgType === "success" ? "green" : "red",
                whiteSpace: "pre-line",
              }}
            >
              {message}
            </p>
          )}

          <p
            style={{
              marginTop: "15px",
              textAlign: "center",
              fontSize: "14px",
            }}
          >
            Don&apos;t have an account?{" "}
            <Link to="/register" style={{ color: "#4CAF50" }}>
              Register
            </Link>
          </p>
        </div>
      </main>
    </div>
  );
};

export default LoginPage;
