// src/pages/HomePage.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // For redirection
import axios from 'axios'; // For making API calls
import '../styles/Home.css'; // Import the CSS file

// Placeholder images
const infonestLogo = '/logo.png';
const userAvatar = '/avatar.png';

const HomePage = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('User');
  // messages state: array of objects { sender: 'user'/'bot', text: '...' }
  const [messages, setMessages] = useState([]);
  // chatInput state removed as there is no input field
  // const [chatInput, setChatInput] = useState('');
  const [sidebarHistory, setSidebarHistory] = useState([]); // For the Recent list in sidebar

  const API_BASE_URL = 'http://localhost:8000/api';

  // --- Authentication Check and Fetch Initial Data on Mount ---
  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUsername = localStorage.getItem('username');

    if (!token) {
      alert('You are not logged in. Please log in first.');
      navigate('/login');
      return;
    }

    if (storedUsername) {
      setUsername(storedUsername);
    }

    // Fetch history when component mounts
    fetchChatHistory(token);

  }, [navigate]);

  // --- Function to Fetch Chat History ---
  const fetchChatHistory = async (token) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/history`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.status === 200 && response.data.success) {
        const historyData = response.data.data;
        const formattedMessages = [];
        const newSidebarHistory = [];

        historyData.forEach(entry => {
          formattedMessages.push({ sender: 'user', text: entry.userMessage });
          formattedMessages.push({ sender: 'bot', text: entry.botResponse });
          // Add truncated user message to sidebar history
          newSidebarHistory.push(entry.userMessage.substring(0, 30) + (entry.userMessage.length > 30 ? '...' : ''));
        });

        setMessages(formattedMessages);
        setSidebarHistory(newSidebarHistory); // Update sidebar state

        // If no history, show a greeting from the bot
        if (formattedMessages.length === 0) {
          setMessages([{ sender: 'bot', text: 'Hello! How can I help you today?' }]);
        }
      } else {
        console.error('Failed to fetch chat history:', response.data.error);
        alert('Could not load chat history. Please try again.');
        setMessages([{ sender: 'bot', text: 'Hello! How can I help you today?' }]);
      }
    } catch (error) {
      console.error('Network error fetching chat history:', error);
      if (error.response && error.response.status === 401) {
        alert('Session expired. Please log in again.');
        localStorage.removeItem('token');
        localStorage.removeItem('username');
        navigate('/login');
      } else {
        alert('Network error. Could not connect to server for history.');
        setMessages([{ sender: 'bot', text: 'Hello! How can I help you today?' }]); // Fallback greeting
      }
    }
  };

  // --- Removed sendMessage function, input handling, and send button ---

  // --- Event Handlers ---
  // Removed: handleChatInputChange, handleSendButtonClick, handleInputKeyPress

  // --- Handle Clicking on Quick Question Buttons ---
  // You might want to keep these if they are supposed to pre-fill the input,
  // but if there's no input, they might not be functional without a send mechanism.
  // For now, let's comment them out or handle them differently if they are needed.
  /*
  const handleQuickQuestionClick = (e) => {
    sendMessage(e.target.textContent); // This would try to send, but sendMessage is removed.
  };
  */

  // --- Auto-scroll messages to bottom ---
  useEffect(() => {
    const messagesContainer = document.querySelector('.chat-messages-container ul');
    if (messagesContainer) {
      messagesContainer.scrollTop = messagesContainer.scrollHeight;
    }
  }, [messages]);

  // --- Removed "New chat" button functionality as it implies starting new conversation ---
  // If you want a "clear history" button, you'd need a different UX flow.
  // For now, we'll just display loaded history.

  // Render JSX
  return (
    <div className="container">
      <aside className="sidebar">
        <div className="logo-section">
          <img src={infonestLogo} alt="InfoNest Logo" className="circle-logo" />
          <h1>InfoNest</h1>
        </div>

        {/* Removed New Chat Button */}
        {/* <button className="new-chat-btn" onClick={handleNewChat}> ... </button> */}

        <p className="recent-title">Recent</p>
        <ul className="recent-list">
          {/* Render recent chat history */}
          {sidebarHistory.map((entry, index) => (
            <li
              key={index}
              // Removed onClick for recent items as there's no send mechanism and we're not loading specific chats
              style={{
                cursor: 'default', // Changed cursor to default as they are not interactive now
                padding: '5px 0', fontSize: '14px', lineHeight: '1.5', color: '#333'
              }}
            >
              {entry}
            </li>
          ))}
        </ul>

        <div className="settings-footer">
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ marginRight: '8px' }}>
            <path d="M19.4286 4.57143C19.4286 4.57143 15.2143 0.571429 12 0.571429C8.78571 0.571429 4.57143 4.57143 4.57143 4.57143M19.4286 4.57143V10.2857C19.4286 10.7857 19.0714 11.1429 18.5714 11.1429H17.2857M19.4286 4.57143V8.57143M4.57143 4.57143V10.2857C4.57143 10.7857 4.92857 11.1429 5.42857 11.1429H6.71429M4.57143 4.57143V8.57143M12 11.1429V23.4286M12 11.1429C10.2857 11.1429 8.57143 11.1429 7.14286 10.2857C5.71429 9.42857 5.71429 7.71429 5.71429 6.28571C5.71429 4.85714 5.71429 3.14286 7.14286 2.28571C8.57143 1.42857 10.2857 1.42857 12 1.42857C13.7143 1.42857 15.4286 1.42857 16.8571 2.28571C18.2857 3.14286 18.2857 4.85714 18.2857 6.28571C18.2857 7.71429 18.2857 9.42857 16.8571 10.2857" stroke="#555" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          Settings & Help
        </div>
      </aside>

      <main className="main-content">
        <div className="avatar-container">
          <img src={userAvatar} alt="User Avatar" className="avatar-img" />
        </div>
        <h1 className="greeting">Hello, <span id="userNameGreeting">{username}</span>!</h1>

        {/* Chat messages display area */}
        <div className="chat-messages-container" style={{ /* Styling moved here for clarity, ideally in CSS */
            maxHeight: '400px',
            overflowY: 'auto',
            width: '70%', // Adjusted width to be a bit smaller
            marginBottom: '20px',
            border: '1px solid #ccc',
            padding: '10px',
            borderRadius: '8px',
            backgroundColor: '#f9f9f9'
        }}>
          <ul id="messagesList" style={{ listStyle: 'none', padding: '0', margin: '0' }}>
            {/* Render messages */}
            {messages.map((msg, index) => (
              <li
                key={index}
                // Removed onClick as these are just for display now
                style={{
                  marginBottom: '10px',
                  padding: '8px 12px',
                  borderRadius: '15px',
                  maxWidth: '70%',
                  wordWrap: 'break-word',
                  backgroundColor: msg.sender === 'user' ? '#dcf8c6' : '#e0e0e0', // User: light green, Bot: light grey
                  marginLeft: msg.sender === 'user' ? 'auto' : '0',
                  marginRight: msg.sender === 'user' ? '0' : 'auto',
                  textAlign: msg.sender === 'user' ? 'right' : 'left',
                  // display: 'inline-block', // Removed as it conflicts with margin: auto for user
                  // paddingLeft: msg.sender === 'user' ? '15px' : '12px',
                  // paddingRight: msg.sender === 'user' ? '12px' : '15px',
                }}
              >
                {msg.text}
              </li>
            ))}
          </ul>
        </div>

        {/* Removed the buttons-row section entirely */}

        {/* Removed the input and send button area */}
        {/*
        <div className="ask-container">
          <input
            type="text"
            id="chatInput"
            placeholder="Ask InfoNest anything"
            value={chatInput}
            onChange={handleChatInputChange}
            onKeyPress={handleInputKeyPress}
            style={{ ... }}
          />
          <button id="sendChatBtn" onClick={handleSendButtonClick} style={{ ... }}>Send</button>
        </div>
        */}
      </main>
    </div>
  );
};

export default HomePage;
