// src/pages/HomePage.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // For redirection
import axios from 'axios'; // For making API calls
import '../styles/Home.css'; // Import the CSS file

// Placeholder images
const infonestLogo = '/logo.png'; // Assuming logo.png is in public/
const userAvatar = '/avatar.png'; // Assuming avatar.png is in public/

const HomePage = () => {
  const navigate = useNavigate(); // Hook for navigation
  const [username, setUsername] = useState('User');
  const [messages, setMessages] = useState([]); // Stores { sender: 'user'/'bot', text: '...' }
  const [chatInput, setChatInput] = useState('');
  const [sidebarHistory, setSidebarHistory] = useState([]); // For the Recent list in sidebar

  const API_BASE_URL = 'http://localhost:8000/api';

  const initialLoadCompleted = React.useRef(false); // Renamed for clarity

useEffect(() => {
  const token = localStorage.getItem('token');
  const storedUsername = localStorage.getItem('username');

  if (!token) {
    alert('You are not logged in. Please log in first.');
    navigate('/login');
    return;
  }

  if (storedUsername) {
    setUsername(storedUsername);
  }

  // Fetch conversations only if initial load hasn't happened yet
  // and we have a token.
  if (!initialLoadDone.current) {
      fetchConversations(token); // Fetch conversations for sidebar and load initial chat
      initialLoadDone.current = true; // Mark that initial load has been triggered
  }
}, [navigate, token]); // Depend on navigate and token to re-run if auth state changes

// --- fetchConversations function (as provided before) ---
const fetchConversations = async (token) => {
    try {
        const response = await axios.get(`${API_BASE_URL}/conversations`, {
            headers: { Authorization: `Bearer ${token}` },
        });

        if (response.status === 200 && response.data.success) {
            const conversations = response.data.data;
            setSidebarHistory(conversations);

            // If there are conversations, load the most recent one by default
            if (conversations.length > 0 && !currentConversationId) { // Only load if no conversation is currently active
                loadConversation(conversations[0].id, token);
            } else if (conversations.length === 0) {
                // If no conversations exist yet, show an initial bot greeting
                setMessages([{ sender: 'bot', text: 'Hello! How can I help you today?' }]);
            }
        } else {
            console.error('Failed to fetch conversations:', response.data.error);
            alert('Could not load conversation history. Please try again.');
            setMessages([{ sender: 'bot', text: 'Hello! How can I help you today?' }]);
        }
    } catch (error) {
        console.error('Network error fetching conversations:', error);
        if (error.response && error.response.status === 401) { // Session expired
            alert('Session expired. Please log in again.');
            localStorage.removeItem('token');
            localStorage.removeItem('username');
            navigate('/login');
        } else {
            alert('Network error. Could not connect to server for conversations.');
            setMessages([{ sender: 'bot', text: 'Hello! How can I help you today?' }]);
        }
    }
};

  const sendMessage = async (messageText) => {
    const token = localStorage.getItem('token');
    if (!token || messageText.trim() === '') return;

    const userMessage = { sender: 'user', text: messageText };
    setMessages((prevMessages) => [...prevMessages, userMessage]);

    setChatInput('');

    try {
      const response = await axios.post(`${API_BASE_URL}/chat`,
        { message: messageText },
        { headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'application/json' } }
      );

      if (response.status === 200) {
        const botResponse = response.data.botResponse;
        setMessages((prevMessages) => [...prevMessages, { sender: 'bot', text: botResponse }]);
        setSidebarHistory(prevSidebar => [...prevSidebar, messageText.substring(0, 25) + '...']);
      } else {
        const errorData = response.data;
        setMessages((prevMessages) => [...prevMessages, { sender: 'bot', text: `Error: ${errorData.error || 'Failed to get bot response.'}` }]);
      }
    } catch (error) {
      console.error('Network error sending message:', error);
      let errorMessage = 'Network error. Could not connect to chatbot.';
      if (error.response && error.response.status === 401) {
         errorMessage = 'Session expired. Please log in again.';
         localStorage.removeItem('token');
         localStorage.removeItem('username');
         navigate('/login');
      } else if (error.response && error.response.data && error.response.data.error) {
         errorMessage = `Error: ${error.response.data.error}`;
      }
      setMessages((prevMessages) => [...prevMessages, { sender: 'bot', text: errorMessage }]);
    }
  };

  // --- Function to handle starting a new chat ---
  const handleNewChat = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
        alert('You are not logged in. Please log in first.');
        navigate('/login');
        return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}/chat/new`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (response.status === 201) {
        const newConversationId = response.data.conversationId;
        setCurrentConversationId(newConversationId); // <-- Set the new conversation ID
        setMessages([]); // Clear current chat display
        setSidebarHistory([]); // Clear sidebar history to reflect a fresh start
        setMessages([{ sender: 'bot', text: response.data.initialMessage || 'Hello! How can I help you today?' }]);
        initialLoadDone.current = false; // Allow fetching conversations again if needed, though usually not after new chat
      } else {
        alert('Failed to start new chat. Please try again.');
      }
    } catch (error) {
      console.error('Error starting new chat:', error);
      alert('Network error starting new chat.');
    }
  };

// --- Modify loadConversation to set currentConversationId ---
const loadConversation = async (conversationId, token) => {
    if (!conversationId) return;

    setCurrentConversationId(conversationId); // <-- Set the current conversation ID
    // ... (rest of loadConversation remains the same) ...
    try {
      const response = await axios.get(`${API_BASE_URL}/conversation/${conversationId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.status === 200 && response.data.success) {
        const fetchedMessages = [];
        response.data.data.forEach(entry => {
          if (entry.userMessage) {
            formattedMessages.push({ sender: 'user', text: entry.userMessage });
          }
          if (entry.botResponse) {
            formattedMessages.push({ sender: 'bot', text: entry.botResponse });
          }
        });
        setMessages(formattedMessages);
      } else {
        console.error('Failed to fetch specific conversation:', response.data.error);
        alert('Could not load this conversation history.');
        setMessages([{ sender: 'bot', text: 'Failed to load conversation.' }]);
      }
    } catch (error) {
      console.error('Network error fetching conversation:', error);
      if (error.response && error.response.status === 401) { // Session expired
        alert('Session expired. Please log in again.');
        localStorage.removeItem('token');
        localStorage.removeItem('username');
        navigate('/login');
      } else {
        alert('Network error. Could not connect to server for conversation.');
      }
    }
};

  useEffect(() => {
    const messagesContainer = document.querySelector('.chat-messages-container');
    if (messagesContainer) {
      messagesContainer.scrollTop = messagesContainer.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="container">
      <aside className="sidebar">
        <div className="logo-section">
          <img src={infonestLogo} alt="InfoNest Logo" className="circle-logo" />
          <h1>InfoNest</h1>
        </div>

        {/* --- Modified Button to call handleNewChat --- */}
        <button className="new-chat-btn" onClick={handleNewChat}>
          {/* Pencil SVG for New Chat */}
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M12 5V19M19 12H5" stroke="#333" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          New chat
        </button>

        <p className="recent-title">Recent</p>
        <ul className="recent-list">
          {sidebarHistory.map((entry, index) => (
            <li key={index}>{entry}</li>
          ))}
        </ul>

        <div className="settings-footer">
          {/* Gear SVG for Settings */}
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M19.4286 4.57143C19.4286 4.57143 15.2143 0.571429 12 0.571429C8.78571 0.571429 4.57143 4.57143 4.57143 4.57143M19.4286 4.57143V10.2857C19.4286 10.7857 19.0714 11.1429 18.5714 11.1429H17.2857M19.4286 4.57143V8.57143M4.57143 4.57143V10.2857C4.57143 10.7857 4.92857 11.1429 5.42857 11.1429H6.71429M4.57143 4.57143V8.57143M12 11.1429V23.4286M12 11.1429C10.2857 11.1429 8.57143 11.1429 7.14286 10.2857C5.71429 9.42857 5.71429 7.71429 5.71429 6.28571C5.71429 4.85714 5.71429 3.14286 7.14286 2.28571C8.57143 1.42857 10.2857 1.42857 12 1.42857C13.7143 1.42857 15.4286 1.42857 16.8571 2.28571C18.2857 3.14286 18.2857 4.85714 18.2857 6.28571C18.2857 7.71429 18.2857 9.42857 16.8571 10.2857" stroke="#555" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          Settings & Help
        </div>
      </aside>

      <main className="main-content">
        {/* Greeting and Avatar removed */}

        {/* Chat messages display area */}
        <div className="chat-messages-container">
          <ul>
            {messages.map((msg, index) => (
              <li
                key={index}
                className={msg.sender === 'user' ? 'user-message' : 'bot-message'}
              >
                {msg.text}
              </li>
            ))}
          </ul>
        </div>

        {/* Removed the buttons-row section entirely */}

        <div className="ask-container">
          <input
            type="text"
            id="chatInput"
            placeholder="Ask InfoNest anything"
            value={chatInput}
            onChange={handleChatInputChange}
            onKeyPress={handleInputKeyPress}
          />
          <button id="sendChatBtn" onClick={handleSendButtonClick}>Send</button>
        </div>
      </main>
    </div>
  );
};

